package MySnake;

public class Food {
	private int x=0;
	private int y=0;
	public Food() {
		// TODO Auto-generated constructor stub
	}
	public Food(int x,int y) {
		this.x=x;
		this.y=y;
	}
	public int getX() {
		return x;
	}
	public void setX(int x) {
		this.x = x;
	}
	public int getY() {
		return y;
	}
	public void setY(int y) {
		this.y = y;
	}
	
		
}
